    public static int getConsecutiveEncodableCharacters(String input) {
        int count = 0; // Counter for consecutive encodable characters
        int maxCount = 0; // Maximum count of consecutive encodable characters

        for (int i = 0; i < input.length(); i++) {
            char currentChar = input.charAt(i);
            if (Character.isDigit(currentChar)) {
                count++;
            } else {
                maxCount = Math.max(maxCount, count);
                count = 0; // Reset count when non-numeric character is encountered
            }
        }

        return maxCount;
    }

