public static int decodeInteger(String format, boolean advance) {
    int result = 0;
    int index = 0;
    char ch = format.charAt(index);

    if (!Character.isDigit(ch)) {
        return 0;
    }

    while (Character.isDigit(ch)) {
        result = result * 10 + Character.getNumericValue(ch);
        index++;

        if (index < format.length()) {
            ch = format.charAt(index);
        } else {
            break;
        }
    }

    if (advance) {
        index++;
    }

    return result;
}

