import org.apache.commons.math3.linear.EigenDecomposition;
import org.apache.commons.math3.linear.MatrixUtils;
import org.apache.commons.math3.linear.RealMatrix;
import org.apache.commons.math3.complex.Complex;

public class EigenvalueCalculator {

  /**
   * Returns the eigenvalue of a symmetric matrix as a complex number.
   * For symmetric matrices, the returned eigenvalue will always be real
   * with an imaginary component equal to zero.
   *
   * @param matrix The symmetric matrix for which to calculate the eigenvalue.
   * @return The eigenvalue as a Complex number.
   */
  public static Complex calculateEigenvalue(RealMatrix matrix) {
    // Perform eigenvalue decomposition
    EigenDecomposition eigenDecomposition = new EigenDecomposition(matrix);

    // Get the eigenvalues
    Complex[] eigenvalues = eigenDecomposition.getEigenvalues().toArray();

    // Since the matrix is symmetric, eigenvalues are guaranteed to be real or come in complex conjugate pairs
    // We choose the first eigenvalue as the result
    Complex eigenvalue = eigenvalues[0];

    return eigenvalue;
  }

  public static void main(String[] args) {
    // Example usage:
    double[][] matrixData = {{1.0, 2.0}, {2.0, 3.0}}; // Example symmetric matrix
    RealMatrix matrix = MatrixUtils.createRealMatrix(matrixData);
    Complex eigenvalue = calculateEigenvalue(matrix);
    System.out.println("Eigenvalue: " + eigenvalue);
  }
}

