public class PolicyUtils {
    /**
     * Sets the policy combining algorithm globally.
     * @param algorithm the algorithm to set
     */
    public static void setPolicyCombiningAlgorithm(String algorithm) throws LoginException {
        // Create a login context to obtain the current security policy
        LoginContext lc = new LoginContext("PolicyUtils");
        lc.login();

        // Get the current policy and parameters
        Policy policy = Policy.getPolicy();
        PolicyParameters params = policy.getParameters();

        // Set the policy combining algorithm
        params.setProperty("policy.algorithm", algorithm);

        // Update the policy with the new parameters
        policy.refresh(params);

        // Logout of the login context
        lc.logout();
    }
}

