import java.math.BigInteger;
import java.security.KeyPair;
import java.security.KeyPairGenerator;
import java.security.PublicKey;
import javax.crypto.KeyAgreement;

public class KeyAgreementExample {

    public static BigInteger generatePublicValue() throws Exception {

        // Generate the key pair for the local party
        KeyPairGenerator keyPairGenerator = KeyPairGenerator.getInstance("DH");
        keyPairGenerator.initialize(2048);
        KeyPair localKeyPair = keyPairGenerator.generateKeyPair();

        // Create a KeyAgreement object and initialize it with the local private key
        KeyAgreement keyAgreement = KeyAgreement.getInstance("DH");
        keyAgreement.init(localKeyPair.getPrivate());

        // Generate the public key for the local party
        PublicKey localPublicKey = localKeyPair.getPublic();

        // Generate the public value for the key agreement operation
        keyAgreement.doPhase(localPublicKey, true);
        byte[] sharedSecret = keyAgreement.generateSecret();

        // Convert the shared secret to a BigInteger
        BigInteger publicValue = new BigInteger(1, sharedSecret);

        return publicValue;
    }
}

