import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;

public class EntrezGeneIdGetter {

    private static final String BASE_URL = "https://eutils.ncbi.nlm.nih.gov/entrez/eutils/";

    public static String getEntrezGeneId(String geneName) {
        String entrezGeneId = null;
        try {
            String esearchUrl = BASE_URL + "esearch.fcgi?db=gene&term=" + geneName + "[Gene%20Name]";
            URL url = new URL(esearchUrl);
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("GET");
            BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream()));
            String line;
            StringBuilder response = new StringBuilder();
            while ((line = reader.readLine()) != null) {
                response.append(line);
            }
            reader.close();
            String xml = response.toString();
            int startIndex = xml.indexOf("<Id>");
            int endIndex = xml.indexOf("</Id>");
            if (startIndex != -1 && endIndex != -1) {
                entrezGeneId = xml.substring(startIndex + 4, endIndex);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return entrezGeneId;
    }

}

