public static Document createDocumentFromSource(Source source) throws TransformerException, ParserConfigurationException {
    if (source instanceof DOMSource) {
        DOMSource domSource = (DOMSource) source;
        if (domSource.getNode() instanceof Document) {
            return (Document) domSource.getNode();
        }
    }
    DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
    factory.setNamespaceAware(true);
    DocumentBuilder builder = factory.newDocumentBuilder();
    InputSource inputSource = toInputSource(source);
    Document document = builder.newDocument();
    DOMResult result = new DOMResult(document);
    Transformer transformer = TransformerFactory.newInstance().newTransformer();
    transformer.transform(inputSource, result);
    return document;
}

private static InputSource toInputSource(Source source) throws TransformerException {
    TransformerFactory factory = TransformerFactory.newInstance();
    Transformer transformer = factory.newTransformer();
    transformer.setOutputProperty(OutputKeys.METHOD, "xml");
    transformer.setOutputProperty(OutputKeys.OMIT_XML_DECLARATION, "yes");
    transformer.setOutputProperty(OutputKeys.INDENT, "no");
    StringWriter writer = new StringWriter();
    transformer.transform(source, new StreamResult(writer));
    String xml = writer.toString();
    InputSource inputSource = new InputSource(new StringReader(xml));
    return inputSource;
}

