public class TimeUtils {

  public static String getISO8601Time() {
    // Get the current time
    LocalTime localTime = LocalTime.now();

    // Get the offset from UTC
    ZoneOffset zoneOffset = OffsetTime.now().getOffset();

    // Create an OffsetTime object with the current time and offset
    OffsetTime offsetTime = OffsetTime.of(localTime, zoneOffset);

    // Format the OffsetTime object as an ISO 8601 string
    DateTimeFormatter formatter = DateTimeFormatter.ISO_OFFSET_TIME;
    String isoTime = offsetTime.format(formatter);

    return isoTime;
  }

}

