import alluxio.client.file.policy.FileWriteLocationPolicy;
import alluxio.exception.ExceptionMessage;

public class AlluxioUtils {
    public static OpenFileOptions convertToOpenFileOptions(CreateFileOptions createOptions) {
        OpenFileOptions openOptions = new OpenFileOptions();
        switch (createOptions.getWriteType()) {
            case CACHE_THROUGH:
            case MUST_CACHE:
                openOptions.setReadType(ReadType.CACHE_PROMOTE);
                openOptions.setCacheable(true);
                break;
            case THROUGH:
                openOptions.setReadType(ReadType.NO_CACHE);
                openOptions.setCacheable(false);
                break;
            default:
                throw new IllegalArgumentException(
                        ExceptionMessage.UNSUPPORTED_WRITE_TYPE.getMessage(createOptions.getWriteType()));
        }
        FileWriteLocationPolicy policy = createOptions.getLocationPolicy();
        if (policy != null) {
            openOptions.setLocationPolicy(policy);
        } else {
            openOptions.setUfsPath(createOptions.getUfsPath());
        }
        return openOptions;
    }
}

