import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.NodeList;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import org.apache.xml.security.encryption.EncryptedData;
import org.apache.xml.security.encryption.XMLCipher;
import org.apache.xml.security.utils.EncryptionConstants;

public class KeyInfoExtractor {

  public static Element getKeyInfo(String xmlString) throws Exception {
    DocumentBuilderFactory dbf = DocumentBuilderFactory.newInstance();
    dbf.setNamespaceAware(true);
    DocumentBuilder builder = dbf.newDocumentBuilder();
    Document doc = builder.parse(new InputSource(new StringReader(xmlString)));

    Element encryptedDataElement = (Element) doc.getElementsByTagNameNS(
        EncryptionConstants.EncryptionSpecNS,
        EncryptionConstants._TAG_ENCRYPTEDDATA).item(0);

    XMLCipher xmlCipher = XMLCipher.getInstance();
    xmlCipher.init(XMLCipher.DECRYPT_MODE, null);
    EncryptedData encryptedData = xmlCipher.loadEncryptedData(doc, encryptedDataElement);
    xmlCipher.init(XMLCipher.UNWRAP_MODE, encryptedData.getEncryptionMethod().getAlgorithm(), null);
    xmlCipher.setKEK(xmlCipher.getNewRecipientsKeyInfo().item(0));

    NodeList keyInfoNodes = encryptedData.getKeyInfo().getChildNodes();

    for (int i = 0; i < keyInfoNodes.getLength(); i++) {
      if (keyInfoNodes.item(i) instanceof Element) {
        return (Element) keyInfoNodes.item(i);
      }
    }

    return null;
  }
}

