import java.util.Base64;
import javax.crypto.Mac;
import javax.crypto.spec.SecretKeySpec;

public static boolean verifyJwt(String jwt, String secret) {
    String[] jwtParts = jwt.split("\\.");
    if (jwtParts.length != 3) {
        return false;
    }

    String header = jwtParts[0];
    String payload = jwtParts[1];
    String signature = jwtParts[2];

    String expectedSignature;
    try {
        Mac sha256Hmac = Mac.getInstance("HmacSHA256");
        SecretKeySpec secretKey = new SecretKeySpec(secret.getBytes(), "HmacSHA256");
        sha256Hmac.init(secretKey);
        byte[] signatureBytes = sha256Hmac.doFinal((header + "." + payload).getBytes());
        expectedSignature = Base64.getUrlEncoder().withoutPadding().encodeToString(signatureBytes);
    } catch (NoSuchAlgorithmException | InvalidKeyException e) {
        return false;
    }

    return expectedSignature.equals(signature);
}

