        this.uriToSqlMap = new HashMap<>();
    }

    public void addMapping(String uri, String sql) {
        // Guess the base type and sub type of the tables being joined
        String[] tableNames = sql.split("(?i)JOIN");
        String[] firstTableNameParts = tableNames[0].trim().split(" ");
        String[] lastTableNameParts = tableNames[tableNames.length - 1].trim().split(" ");
        String baseType = firstTableNameParts[firstTableNameParts.length - 1];
        String subType = lastTableNameParts[lastTableNameParts.length - 1];

        // Add the mapping to the map
