    ScriptEngineManager engineManager = new ScriptEngineManager();
    ScriptEngine engine = engineManager.getEngineByName("JavaScript");
    if (engine instanceof Compilable) {
        Compilable compilable = (Compilable) engine;
        try {
            String script = "var a = b"; // an example script that throws a reference error
            CompiledScript compiledScript = compilable.compile(script);
            if (breakOnException) {
                // sets an error handler that throws a ScriptException when a script error occurs
                compiledScript.eval(new SimpleScriptContext() {
                    @Override
                    public void setErrorWriter(Writer writer) {
                        super.setErrorWriter(writer);
                    }
                    @Override
                    public void setAttribute(String name, Object value, int scope) {
                        if (name.equals("javax.script.script_exception")) {
                            throw (ScriptException) value;
                        } else {
                            super.setAttribute(name, value, scope);
                        }
                    }
                });
