import org.springframework.security.oauth2.provider.ClientDetailsService;
import org.springframework.security.oauth2.provider.OAuth2Authentication;
import org.springframework.security.oauth2.provider.token.TokenStore;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.ArrayList;
import java.util.List;
import java.util.Set;

@RestController
public class ScopeController {

    private final TokenStore tokenStore;
    private final ClientDetailsService clientDetailsService;

    public ScopeController(TokenStore tokenStore, ClientDetailsService clientDetailsService) {
        this.tokenStore = tokenStore;
        this.clientDetailsService = clientDetailsService;
    }

    @GetMapping("/scopes")
    public List<String> getRegisteredScopes() {
        List<String> scopes = new ArrayList<>();

        // Get all active OAuth2 authentications
        Set<OAuth2Authentication> authentications = tokenStore.findActiveAuthentications();

        for (OAuth2Authentication authentication : authentications) {
            // Get the authorized client's details
            String clientId = authentication.getOAuth2Request().getClientId();
            // Get the registered scopes for the client
            Set<String> clientScopes = clientDetailsService.loadClientByClientId(clientId).getScope();

            // Add each scope to the list of registered scopes
            for (String scope : clientScopes) {
                if (!scopes.contains(scope)) {
                    scopes.add(scope);
                }
            }
        }

        return scopes;
    }
}

