public static boolean isBridgeForMethod(Method bridgeMethod, Method candidateMethod) {
    // Check if the bridge method is a bridge method
    if (!bridgeMethod.isBridge()) {
        return false;
    }
    // Check if the bridge method has the same name and return type as the candidate method
    if (!bridgeMethod.getName().equals(candidateMethod.getName())
            || !bridgeMethod.getReturnType().equals(candidateMethod.getReturnType())) {
        return false;
    }
    // Check if the bridge method has the same parameter types as the candidate method
    Class<?>[] bridgeParamTypes = bridgeMethod.getParameterTypes();
    Class<?>[] candidateParamTypes = candidateMethod.getParameterTypes();
    if (bridgeParamTypes.length != candidateParamTypes.length) {
        return false;
    }
    for (int i = 0; i < bridgeParamTypes.length; i++) {
        if (!bridgeParamTypes[i].equals(candidateParamTypes[i])) {
            return false;
        }
    }
    // Check if the bridge method is defined in the same class or a subclass of the candidate method's declaring class
    Class<?> candidateDeclaringClass = candidateMethod.getDeclaringClass();
    Class<?> bridgeDeclaringClass = bridgeMethod.getDeclaringClass();
    return candidateDeclaringClass.isAssignableFrom(bridgeDeclaringClass);
}

