import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.attribute.BasicFileAttributes;
import java.util.HashSet;
import java.util.Set;

public class FileUtil {

  /**
   * Add a file to a set only once, even if it is found multiple times due to a symbolic link.
   * 
   * @param file the file to be added
   * @param set the set to which the file should be added
   * @throws IOException if there is an error accessing the file
   */
  public static void addFileOnlyOnce(File file, Set<File> set) throws IOException {
    Path path = file.toPath().toRealPath(); // resolves symbolic links

    // Check if the file has already been added to the set
    for (File addedFile : set) {
      Path addedPath = addedFile.toPath().toRealPath(); // resolves symbolic links
      if (Files.isSameFile(path, addedPath)) {
        return; // file has already been added, so return without doing anything
      }
    }

    // Add the file to the set
    set.add(file);
  }

  // Example usage:
  public static void main(String[] args) throws IOException {
    Set<File> files = new HashSet<>();
    File file1 = new File("/path/to/file");
    File file2 = new File("/path/to/symlink");
    addFileOnlyOnce(file1, files);
    addFileOnlyOnce(file2, files);
    System.out.println(files.size()); // should print "1"
  }
}

