import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.net.URL;

public class FileUtils {

    public static InputStream findFile(String filename) throws IOException {
        // try finding the file as a regular file first
        File file = new File(filename);
        if (file.exists() && file.isFile()) {
            return file.toURI().toURL().openStream();
        }

        // try finding the file as a URL next
        URL url = new URL(filename);
        InputStream stream = url.openStream();
        if (stream != null) {
            return stream;
        }

        // try finding the file on the classpath
        ClassLoader loader = Thread.currentThread().getContextClassLoader();
        stream = loader.getResourceAsStream(filename);
        if (stream != null) {
            return stream;
        }

        // if all else fails, throw an exception
        throw new IOException("File not found: " + filename);
    }
}

