    FileInputStream fis = new FileInputStream(certFilePath);
    ks.load(fis, password.toCharArray());
    fis.close();

    // Assuming there's only one certificate in the keystore
    String alias = null;
    X509Certificate cert = (X509Certificate) ks.getCertificate(ks.aliases().nextElement());
    if (cert != null) {
        alias = ks.getCertificateAlias(cert);
    }
    return alias;
}

