public static boolean interpretBoolean(String currentValue, boolean defaultValue) {
    if (currentValue == null) {
        return defaultValue;
    }
    switch (currentValue.toLowerCase()) {
        case "true":
        case "yes":
        case "1":
            return true;
        case "false":
