public void resetSeekBar(SeekBar seekBar, int originalProgress) {
    seekBar.setOnSeekBarChangeListener(null); // Remove the listener temporarily

    int currentProgress = seekBar.getProgress();
    if (currentProgress == originalProgress) {
        return; // No need to reset if already in original position
    }

    // Simulate progress change to original position
    seekBar.setProgress(originalProgress + 1);
    seekBar.setProgress(originalProgress);

    // Trigger onProgressChanged() with original position values
    seekBar.setOnSeekBarChangeListener(new SeekBar.OnSeekBarChangeListener() {
        @Override
        public void onProgressChanged(SeekBar seekBar, int progress, boolean fromUser) {
            if (progress == originalProgress) {
                // Restore original listener and stop listening
                seekBar.setOnSeekBarChangeListener(null);
                seekBar.setOnSeekBarChangeListener(this);
                return;
            }

            // Do something with progress value
            // ...
        }

        @Override
        public void onStartTrackingTouch(SeekBar seekBar) {}

        @Override
        public void onStopTrackingTouch(SeekBar seekBar) {}
    });

    // Set the progress to the original position again
    seekBar.setProgress(originalProgress);
}

