import org.json.*;

public class JSONArrayToNodesList {

    public static List<JSONObject> jsonArrayToNodesList(JSONArray jsonArray) throws JSONException {
        List<JSONObject> nodesList = new ArrayList<>();
        for (int i = 0; i < jsonArray.length(); i++) {
            JSONObject node = jsonArray.getJSONObject(i);
            // If the current node has a parent, add it to the parent's "children" array
            if (node.has("parent")) {
                JSONObject parent = findNodeById(nodesList, node.getString("parent"));
                if (parent.has("children")) {
                    parent.getJSONArray("children").put(node);
                } else {
                    JSONArray children = new JSONArray();
                    children.put(node);
                    parent.put("children", children);
                }
            }
            // Otherwise, add it to the root nodes list
            else {
                nodesList.add(node);
            }
        }
        return nodesList;
    }

    private static JSONObject findNodeById(List<JSONObject> nodesList, String id) {
        for (JSONObject node : nodesList) {
            if (node.getString("id").equals(id)) {
                return node;
            }
            // Recursively search for the node in the children of the current node
            if (node.has("children")) {
                JSONObject child = findNodeById(toList(node.getJSONArray("children")), id);
                if (child != null) {
                    return child;
                }
            }
        }
        return null;
    }

    private static List<JSONObject> toList(JSONArray jsonArray) throws JSONException {
        List<JSONObject> list = new ArrayList<>();
        for (int i = 0; i < jsonArray.length(); i++) {
            list.add(jsonArray.getJSONObject(i));
        }
        return list;
    }
}

