import org.mozilla.javascript.*;

public class MyScriptable {

    public static Scriptable wrapObject(Context cx, Scriptable scope, Object javaObject, Class<?> staticType) {
        Scriptable obj = cx.newObject(scope);

        // Define JavaScript getters/setters for each Java field
        Field[] fields = staticType.getDeclaredFields();
        for (Field field : fields) {
            obj.defineOwnProperty(cx, field.getName(),
                    PropertyDescriptor.newDataDescriptor(
                            JavaMembers.javaToJS(field.get(javaObject), scope),
                            ScriptableObject.PERMANENT | ScriptableObject.READONLY
                    ),
                    false
            );
        }

        // Define JavaScript functions for each Java method
        Method[] methods = staticType.getDeclaredMethods();
        for (Method method : methods) {
            obj.put(method.getName(), obj, new JavaMethod(method), ScriptableObject.PERMANENT);
        }

        return obj;
    }

    public static class JavaMethod extends BaseFunction {
        private final Method method;

        public JavaMethod(Method method) {
            this.method = method;
        }

        @Override
        public Object call(Context cx, Scriptable scope, Scriptable thisObj, Object[] args) {
            Object javaObject = Context.jsToJava(thisObj, method.getDeclaringClass());
            try {
                return JavaMembers.javaToJS(method.invoke(javaObject, args), scope);
            } catch (Exception e) {
                throw new RuntimeException("Error invoking Java method", e);
            }
        }
    }
}

