public MultiScan createChunkedScan(Query query, int numBuffers) {
  // Materialize the right-hand side of the query
  String materializedFile = query.materialize();

  // Determine optimal chunk size
  long fileSize = new File(materializedFile).length();
  long chunkSize = fileSize / numBuffers;

  // Create a chunk plan for each chunk
  List<ChunkPlan> chunkPlans = new ArrayList<>();
  long offset = 0;
  while (offset < fileSize) {
    long chunkEnd = Math.min(offset + chunkSize, fileSize);
    ChunkPlan chunkPlan = new ChunkPlan(materializedFile, offset, chunkEnd);
    chunkPlans.add(chunkPlan);
    offset = chunkEnd;
  }

  // Create a multiscan for the list of plans
  MultiScan multiScan = new MultiScan(chunkPlans);

  return multiScan;
}

