/**
 * Checks if two SimpleTypedNameDeclarations are equal based on their types.
 * Two SimpleTypedNameDeclarations are equal if they contain types that can be cast into each other.
 *
 * @param first  the first SimpleTypedNameDeclaration
 * @param second the second SimpleTypedNameDeclaration
 * @return true if the SimpleTypedNameDeclarations are equal, false otherwise
 */
public boolean areTypesEqual(SimpleTypedNameDeclaration first, SimpleTypedNameDeclaration second) {
    if (first == null || second == null) {
        return false;
    }

    Type firstType = first.getType();
    Type secondType = second.getType();

    if (firstType == null || secondType == null) {
        return false;
    }

    if (firstType.equals(secondType)) {
        return true;
    }

    Class<?> firstClass = getClassForType(firstType);
    Class<?> secondClass = getClassForType(secondType);

    return firstClass != null && secondClass != null && firstClass.isAssignableFrom(secondClass);
}

/**
 * Returns the Class object for a given Type.
 *
 * @param type the Type for which to retrieve the Class object
 * @return the Class object, or null if the Type is not a Class or cannot be resolved
 */
private Class<?> getClassForType(Type type) {
    if (type instanceof Class) {
        return (Class<?>) type;
    } else if (type instanceof ParameterizedType) {
        return getClassForType(((ParameterizedType) type).getRawType());
    } else if (type instanceof WildcardType) {
        Type[] upperBounds = ((WildcardType) type).getUpperBounds();
        if (upperBounds.length > 0) {
            return getClassForType(upperBounds[0]);
        }
    }
    return null;
}

