public static void skipBytesAfterChar(InputStream input, char ch, long numBytes) throws IOException {
    // Convert the Unicode character to bytes using UTF-8 encoding
    byte[] charBytes = String.valueOf(ch).getBytes("UTF-8");

    int bytesRead;
    long bytesSkipped = 0;
    boolean foundChar = false;

    // Read from the input stream until the character is found
    while ((bytesRead = input.read()) != -1) {
        byte b = (byte) bytesRead;
        if (b == charBytes[0]) {
            // Check if the entire character has been read
            byte[] buffer = new byte[charBytes.length];
            buffer[0] = b;
            int i = 1;
            while (i < charBytes.length && (bytesRead = input.read()) != -1) {
                buffer[i] = (byte) bytesRead;
                i++;
            }
            if (Arrays.equals(buffer, charBytes)) {
                foundChar = true;
                break;
            }
        }
    }

    // If the character was found, skip the specified number of bytes
    if (foundChar) {
        bytesSkipped = input.skip(numBytes);
    }

    // Print the number of bytes skipped
    System.out.println("Skipped " + bytesSkipped + " bytes after character " + ch);
}

