import java.net.Authenticator;
import java.net.PasswordAuthentication;
import java.net.ProxySelector;
import java.net.URI;

public class ProxyChecker {

  public static boolean hasProxyUsernameAndPassword() {
    Authenticator authenticator = Authenticator.getDefault();
    if (authenticator == null) {
      return false;
    }

    ProxySelector selector = ProxySelector.getDefault();
    if (selector == null) {
      return false;
    }

    URI uri = URI.create("http://www.example.com");
    PasswordAuthentication auth = authenticator.getPasswordAuthentication();
    if (auth == null) {
      return false;
    }

    return selector.select(uri).stream()
        .anyMatch(proxy -> {
          String proxyUser = proxy.address().getUserName();
          String proxyPass = new String(proxy.address().getPassword());
          return proxyUser != null && !proxyUser.isEmpty() && proxyPass != null && !proxyPass.isEmpty();
        });
  }

}

