import java.io.BufferedReader;
import java.io.File;
import java.io.FileReader;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

public class LanguageDetector {

  public static List<String> detectLanguages(String filePath) {
    // Set your API key here
    DetectLanguage.apiKey = "YOUR_API_KEY";

    List<String> languages = new ArrayList<>();

    try (BufferedReader reader = new BufferedReader(new FileReader(new File(filePath)))) {
      StringBuilder sb = new StringBuilder();
      String line;

      // Read the file contents into a StringBuilder
      while ((line = reader.readLine()) != null) {
        sb.append(line);
        sb.append("\n");
      }

      // Detect the language(s) of the text in the StringBuilder
      List<String> detectedLanguages = DetectLanguage.detect(sb.toString());

      // Add the detected languages to the output list
      for (String language : detectedLanguages) {
        languages.add(language);
      }
    } catch (IOException | APIError e) {
      e.printStackTrace();
    }

    return languages;
  }

}

