public static void testGetSatelliteInfo(GSVParser parser) {
    int totalMessages = parser.getTotalMessages();
    int messageNumber = 1; // assuming we are testing the first message

    while (messageNumber <= totalMessages) {
        parser.setSentence("Some GSV sentence for message " + messageNumber); // replace with actual sentence
        int satellitesInView = parser.getSatellitesInView();

        // test if the number of satellites in view is non-negative
        assert(satellitesInView >= 0);

        List<SatelliteInfo> satelliteInfoList = parser.getSatelliteInfo();
        int numSatelliteInfo = satelliteInfoList.size();

        // test if the number of SatelliteInfo objects returned matches the expected number
        assert(numSatelliteInfo == Math.min(4, satellitesInView)); // assuming 4 satellites per message

        for (SatelliteInfo satelliteInfo : satelliteInfoList) {
            int prn = satelliteInfo.getPrn();

            // test if the PRN value is non-negative
            assert(prn >= 0 && prn <= 255);

            int elevation = satelliteInfo.getElevation();

            // test if the elevation value is between 0 and 90
            assert(elevation >= 0 && elevation <= 90);

            int azimuth = satelliteInfo.getAzimuth();

            // test if the azimuth value is between 0 and 359
            assert(azimuth >= 0 && azimuth <= 359);

            int snr = satelliteInfo.getSnr();

            // test if the SNR value is non-negative
            assert(snr >= 0);
        }

        messageNumber++;
    }
}

