import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.attribute.FileAttribute;
import java.nio.file.attribute.PosixFileAttributeView;
import java.nio.file.attribute.PosixFileAttributes;
import java.nio.file.attribute.PosixFilePermission;
import java.util.HashSet;
import java.util.Set;

public class FolderRestorer {

    public static void restoreErrorLevelToOne(String folderPath) throws IOException {

        // Get the folder's PosixFileAttributes
        Path folder = Path.of(folderPath);
        PosixFileAttributeView view = Files.getFileAttributeView(folder, PosixFileAttributeView.class);
        PosixFileAttributes attrs = view.readAttributes();

        // Check if the folder's error level is already set to one
        if (attrs.permissions().contains(PosixFilePermission.OWNER_WRITE)) {
            System.out.println("Error level is already set to one");
            return;
        }

        // Restore the folder's error level to one
        Set<PosixFilePermission> permissions = new HashSet<>();
        permissions.add(PosixFilePermission.OWNER_READ);
        permissions.add(PosixFilePermission.OWNER_WRITE);
        permissions.add(PosixFilePermission.OWNER_EXECUTE);
        FileAttribute<Set<PosixFilePermission>> attr = PosixFilePermissions.asFileAttribute(permissions);
        Files.setPosixFilePermissions(folder, permissions);
        System.out.println("Error level has been set to one");
    }
}

