import java.util.Queue;
import java.util.LinkedList;

public class BackgroundSaver {

    private Queue<SaveTask> saveQueue;

    public BackgroundSaver() {
        saveQueue = new LinkedList<>();
        startSaverThread();
    }

    public void queueSave(Wallet wallet) {
        SaveTask saveTask = new SaveTask(wallet);
        saveQueue.offer(saveTask);
    }

    private void startSaverThread() {
        Thread saverThread = new Thread(() -> {
            while (true) {
                SaveTask saveTask = saveQueue.poll();
                if (saveTask != null) {
                    saveTask.save();
                }
                try {
                    Thread.sleep(1000); // wait for 1 second before checking the queue again
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    return;
                }
            }
        });
        saverThread.start();
    }

    private class SaveTask {
        private Wallet wallet;

        public SaveTask(Wallet wallet) {
            this.wallet = wallet;
        }

        public void save() {
            // Save the wallet here in the background
            System.out.println("Saved wallet: " + wallet.toString());
        }
    }
}

