import java.util.Random;

public class SymmRankTester {
    public static void main(String[] args) {
        Random rand = new Random();
        int[] sizes = {2, 3, 5, 10, 20, 50, 100}; // input matrix sizes to test

        for (int n : sizes) {
            double[][] A = new double[n][n];
            // fill A with random values
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    A[i][j] = rand.nextDouble();
                }
            }
            // compute symmRankNMinus_U and print the result
            System.out.println("For matrix size " + n + ": " + symmRankNMinus_U(A));
        }
    }

    public static int symmRankNMinus_U(double[][] A) {
        int n = A.length;
        int rank = n;
        for (int k = 0; k < n; k++) {
            double max = Math.abs(A[k][k]);
            int maxRow = k;
            // find the row with the maximum absolute value in column k
            for (int i = k + 1; i < n; i++) {
                if (Math.abs(A[i][k]) > max) {
                    max = Math.abs(A[i][k]);
                    maxRow = i;
                }
            }
            // swap rows k and maxRow
            if (maxRow != k) {
                double[] temp = A[k];
                A[k] = A[maxRow];
                A[maxRow] = temp;
            }
            // check if A[k][k] is close to zero
            if (Math.abs(A[k][k]) < 1e-10) {
                rank--;
                continue;
            }
            // eliminate A[i][k] for i > k
            for (int i = k + 1; i < n; i++) {
                double factor = A[i][k] / A[k][k];
                for (int j = k + 1; j < n; j++) {
                    A[i][j] -= factor * A[k][j];
                }
                A[i][k] = 0.0;
            }
        }
        return rank;
    }
}

