public static void createSchedule(String[] activities, int[] startTimes, int[] endTimes) {
    int n = activities.length;
    int[][] schedule = new int[n][2];

    // Sort activities by their end times
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (endTimes[i] > endTimes[j]) {
                int temp1 = endTimes[i];
                int temp2 = startTimes[i];
                int temp3 = schedule[i][0];
