import java.io.IOException;
import org.apache.pdfbox.pdmodel.PDDocument;
import org.apache.pdfbox.pdmodel.interactive.form.PDTextField;
import org.apache.pdfbox.pdmodel.interactive.form.PDNonTerminalField;

public void addRepeatField(String pdfPath, String fieldName, int numRepeats) throws IOException {
    // Load the PDF document
    PDDocument document = PDDocument.load(new File(pdfPath));

    // Get the non-terminal field that contains the repeat field
    PDNonTerminalField parent = (PDNonTerminalField) document.getDocumentCatalog().getAcroForm().getField(fieldName);

    // Create a template text field that will be repeated
    PDTextField templateField = new PDTextField(document.getDocumentCatalog().getAcroForm());
    templateField.setPartialName("template");

    // Add the template field to the parent field
    parent.addKid(templateField);

    // Repeat the template field as many times as specified
    for (int i = 0; i < numRepeats; i++) {
        // Create a new instance of the template field
        PDTextField instanceField = new PDTextField(document.getDocumentCatalog().getAcroForm());
        instanceField.setPartialName("field_" + i);

        // Set the properties of the instance field
        instanceField.setValue(templateField.getValue());
        instanceField.setReadOnly(templateField.isReadOnly());
        instanceField.setRequired(templateField.isRequired());

        // Add the instance field to the parent field
        parent.addKid(instanceField);
    }

    // Save the updated document
    document.save(pdfPath);

    // Close the document
    document.close();
}

