            SequenceFile.Metadata metadata = new SequenceFile.Metadata();
            metadata.set(SequenceFile.Writer.keyClass, objectType.getName());
            metadata.set(SequenceFile.Writer.valueClass, objectType.getName());
            writer = SequenceFile.createWriter(fs, fs.getConf(), path, objectType, objectType);
            writer.sync();
            SequenceFile.Writer.Option[] options = {Writer.fileSystem(fs), Writer.file(path), Writer.metadata(metadata)};
            writer = SequenceFile.createWriter(fs.getConf(), options);
            writer.sync();
        } finally {
            if (writer != null) {
                writer.close();
            }
        }
    }
}

