public static int calculateNextBlockDifficulty(StoredblockCursor cursor, int blocksToLookAt) {
    int totalDifficulty = 0;
    int blocksCounted = 0;

    while (cursor.hasPrevious() && blocksCounted < blocksToLookAt) {
        Block prevBlock = cursor.previous();
        totalDifficulty += prevBlock.getDifficulty();
        blocksCounted++;
    }

    int averageDifficulty = totalDifficulty / blocksCounted;

    // Adjust difficulty to keep block times within a certain range
    int targetTime = 10 * 60; // 10 minutes in seconds
    int adjustmentFactor = Math.max(1, blocksToLookAt / 2); // adjust more for larger blocksToLookAt
    int adjustment = adjustmentFactor * (cursor.getLastBlock().getTimestamp() - cursor.getBlockBefore(blocksToLookAt).getTimestamp() - targetTime);
    int newDifficulty = averageDifficulty + adjustment;

    return newDifficulty;
}

