public static byte hash(byte[] key) {
    final int FNV_PRIME = 16777619;
    final int FNV_OFFSET_BASIS = 216613626;
    int hash = FNV_OFFSET_BASIS;
    for (byte b : key) {
        hash ^= (int) b & 0xff;
        hash *= FNV_PRIME;
    }
    return (byte) ((hash >> 24) ^ (hash >> 16) ^ (hash >> 8) ^ hash);
