public static double computeQRQuality(double[][] A) {
    int m = A.length;
    int n = A[0].length;
    double[][] Q = new double[m][n];
    double[][] R = new double[n][n];

    // Perform QR decomposition
    for (int k = 0; k < n; k++) {
        double norm = 0.0;
        for (int i = k; i < m; i++) {
            norm = Math.hypot(norm, A[i][k]);
        }

        if (norm != 0.0) {
            if (A[k][k] < 0) {
                norm = -norm;
            }

            for (int i = k; i < m; i++) {
                A[i][k] /= norm;
            }

            A[k][k] += 1.0;

            for (int j = k+1; j < n; j++) {
                double s = 0.0;
                for (int i = k; i < m; i++) {
                    s += A[i][k] * A[i][j];
                }
                s = -s/A[k][k];
                for (int i = k; i < m; i++) {
                    A[i][j] += s * A[i][k];
                }
            }
        }

        R[k][k] = -norm;
        for (int i = 0; i < m; i++) {
            Q[i][k] = A[i][k];
        }
    }

    // Compute quality using diagonal elements of R matrix
    double quality = 1.0;
    for (int i = 0; i < n; i++) {
        quality *= R[i][i];
    }
    return quality;
}

