public List<String> getActivePeerIPs(List<String> dnsNames) {
    List<String> activeIps = new ArrayList<>();

    for (String dnsName : dnsNames) {
        try {
            InetAddress[] addresses = InetAddress.getAllByName(dnsName);

            for (InetAddress address : addresses) {
                Socket socket = new Socket();
                try {
                    socket.connect(address, 1000);
                    activeIps.add(address.getHostAddress());
                } catch (IOException e) {
                    // ignore, the IP is not active
                } finally {
                    try {
                        socket.close();
                    } catch (IOException e) {
                        // ignore
                    }
                }
            }
        } catch (UnknownHostException e) {
            // ignore, the DNS name is invalid
        }
    }

    return activeIps;
}

