public static boolean isUsingDFA(String source) {
    // Define the DFA that accepts the language of the rules
    int[][] transitionTable = {
        // State 0: starting state
        {1, -1, -1},
        // State 1: identifier
        {1, 2, 3},
        // State 2: operator
        {-1, -1, -1},
        // State 3: value
        {-1, -1, -1}
    };
    int currentState = 0;

    // Traverse the source string with the DFA
    for (int i = 0; i < source.length(); i++) {
        char c = source.charAt(i);
        int inputSymbol = -1;

        // Map the character to an input symbol
        if (Character.isLetter(c)) {
            inputSymbol = 0;
        } else if (c == '+' || c == '-' || c == '*' || c == '/') {
            inputSymbol = 1;
        } else if (Character.isDigit(c)) {
            inputSymbol = 2;
        }

        // Transition to the next state
        currentState = transitionTable[currentState][inputSymbol];

        // If the current state is invalid, the source string is invalid
        if (currentState == -1) {
            return false;
        }
    }

    // If the final state is accepting, the source string is valid
    return currentState == 2 || currentState == 3;
}

