import java.util.concurrent.TimeoutException;

public class ThreadRunner {
    public static void runWithTimeout(Runnable runnable, long timeoutMillis) throws TimeoutException {
        Thread thread = new Thread(runnable);
        thread.start();
        try {
            thread.join(timeoutMillis);
            if (thread.isAlive()) {
                // Thread did not finish within the timeout
                thread.interrupt(); // Interrupt the thread in case it's stuck
