        // Recursively induce decision trees for each example group
        for (Map.Entry<AttributeValue, List<Example>> entry : exampleGroups.entrySet()) {
            AttributeValue attributeValue = entry.getKey();
            List<Example> exampleGroup = entry.getValue();
