public class LogFileWriter {

  public static void clearAndAppendToFile(String fileName, String logFileName) {
    try {
      // create a File object for the current page
      File pageFile = new File(fileName);

      // create a FileWriter object for the log file
      FileWriter logWriter = new FileWriter(logFileName, true);

      // clear the contents of the page file
      FileWriter pageWriter = new FileWriter(pageFile);
      pageWriter.write("");
      pageWriter.close();

      // append the cleared page file to the log file
      logWriter.write("Contents of " + fileName + " cleared and appended to log file\n");
      logWriter.write("----------------------------------------------\n");
      logWriter.write("Contents of " + fileName + ":\n");
      logWriter.flush();

      // read the contents of the cleared page file and append them to the log file
      java.util.Scanner scanner = new java.util.Scanner(pageFile);
      while (scanner.hasNextLine()) {
        String line = scanner.nextLine();
        logWriter.write(line + "\n");
        logWriter.flush();
      }
      scanner.close();

      // close the FileWriter for the log file
      logWriter.close();

    } catch (IOException e) {
      System.out.println("An error occurred while trying to clear and append to file: " + e.getMessage());
    }
  }

}

