import java.math.BigInteger;

public class GCD {

    public static BigInteger computeGCD(BigInteger num1, BigInteger num2) {
        if (num2.equals(BigInteger.ZERO)) {
            return num1;
        }
        return computeGCD(num2, num1.mod(num2));
    }

    public static void main(String[] args) {
        BigInteger num1 = new BigInteger("105");
        BigInteger num2 = new BigInteger("45");
        BigInteger gcd = computeGCD(num1, num2);
