import java.nio.file.attribute.PosixFilePermission;
import java.nio.file.attribute.PosixFilePermissions;
import java.util.Set;

public class PermissionChecker {
    public static boolean hasPermission(Path path, String action) {
        if (!Files.exists(path)) {
            // The path is invalid, so the check passes
            return true;
        }

        // Check if the user has the required permission to perform the action
        Set<PosixFilePermission> perms = null;
        try {
            perms = Files.getPosixFilePermissions(path);
        } catch (UnsupportedOperationException ex) {
            // This filesystem does not support PosixFilePermissions, so we can't check
            return true;
        } catch (Exception ex) {
            // An error occurred while trying to get the permissions, so the check fails
            return false;
        }

        String requiredPerm = getRequiredPermission(action);
        if (perms.contains(PosixFilePermissions.fromString(requiredPerm))) {
            return true;
        } else {
            return false;
        }
    }

    private static String getRequiredPermission(String action) {
        String requiredPerm;
        switch (action) {
            case "read":
                requiredPerm = "r";
                break;
            case "write":
                requiredPerm = "w";
                break;
            case "execute":
                requiredPerm = "x";
                break;
            default:
                requiredPerm = "";
                break;
        }
        return requiredPerm;
    }
}

