import android.Manifest;
import android.content.pm.PackageManager;
import android.os.Build;
import androidx.annotation.NonNull;
import androidx.annotation.RequiresApi;
import androidx.core.app.ActivityCompat;
import androidx.core.content.ContextCompat;

import java.util.ArrayList;
import java.util.List;

public class PermissionUtils {

    /**
     * Returns a list of the recently granted permissions.
     *
     * @param activity The activity requesting the permissions.
     * @param requestCode The request code passed to the permission request.
     * @param permissions The permissions being requested.
     * @param grantResults The grant results for the corresponding permissions.
     * @return A list of the recently granted permissions.
     */
    @RequiresApi(api = Build.VERSION_CODES.M)
    public static List<String> getRecentlyGrantedPermissions(@NonNull final AppCompatActivity activity,
                                                              final int requestCode,
                                                              @NonNull final String[] permissions,
                                                              @NonNull final int[] grantResults) {
        List<String> grantedPermissions = new ArrayList<>();
        if (requestCode == 0) { // Check if the request code matches the original request code.
            for (int i = 0; i < permissions.length; i++) {
                String permission = permissions[i];
                int grantResult = grantResults[i];
                if (permission.equals(Manifest.permission.SYSTEM_ALERT_WINDOW)) {
                    // Special case for SYSTEM_ALERT_WINDOW permission.
                    if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.M) {
                        if (Settings.canDrawOverlays(activity)) {
                            grantedPermissions.add(permission);
                        }
                    }
                } else {
                    if (grantResult == PackageManager.PERMISSION_GRANTED) {
                        grantedPermissions.add(permission);
                    }
                }
            }
        }
        return grantedPermissions;
    }

    /**
     * Returns true if the app has the specified permission, and false otherwise.
     *
     * @param activity The activity to check the permission for.
     * @param permission The permission to check.
     * @return true if the app has the specified permission, and false otherwise.
     */
    public static boolean hasPermission(@NonNull final AppCompatActivity activity,
                                         @NonNull final String permission) {
        return ContextCompat.checkSelfPermission(activity, permission)
                == PackageManager.PERMISSION_GRANTED;
    }

    /**
     * Requests the specified permissions for the app.
     *
     * @param activity The activity requesting the permissions.
     * @param requestCode The request code to use for the permission request.
     * @param permissions The permissions to request.
     */
    public static void requestPermissions(@NonNull final AppCompatActivity activity,
                                           final int requestCode,
                                           @NonNull final String[] permissions) {
        ActivityCompat.requestPermissions(activity, permissions, requestCode);
    }
}

