import org.ejml.simple.SimpleMatrix;
import org.ejml.simple.SimpleSVD;

public class DiagonalMatrixWithSingularValues {

    public static SimpleMatrix getDiagonalMatrixWithSingularValues(double[][] data) {
        SimpleMatrix matrix = new SimpleMatrix(data);
        SimpleSVD<SimpleMatrix> svd = matrix.svd();
        SimpleMatrix singularValues = svd.getW();

        // Sort the singular values in descending order
        singularValues = singularValues.sortDiag(false);

        // Create a diagonal matrix with the sorted singular values
        int numRows = singularValues.numRows();
        int numCols = singularValues.numCols();
        double[][] diagonalData = new double[numRows][numCols];
        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                diagonalData[i][j] = i == j ? singularValues.get(i, j) : 0;
            }
        }
        return new SimpleMatrix(diagonalData);
    }

    public static void main(String[] args) {
        double[][] data = {{1, 2}, {3, 4}, {5, 6}};
        SimpleMatrix diagonalMatrix = getDiagonalMatrixWithSingularValues(data);
        System.out.println(diagonalMatrix);
    }
}

