import java.util.*;

public class TopKSelector<T> {
    private final PriorityQueue<T> minHeap;
    private final int k;
    private final Comparator<T> comparator;

    public TopKSelector(int k, Comparator<T> comparator) {
        this.k = k;
        this.comparator = comparator;
        this.minHeap = new PriorityQueue<>(k, comparator.reversed());
    }

    public void offer(T element) {
        if (minHeap.size() < k) {
            minHeap.offer(element);
        } else if (comparator.compare(element, minHeap.peek()) > 0) {
            minHeap.poll();
            minHeap.offer(element);
        }
    }

    public List<T> getTopK() {
        List<T> topK = new ArrayList<>(minHeap);
        topK.sort(comparator);
        return Collections.unmodifiableList(topK);
    }
}

