public static String wildcardToRegex(String wildcard) {
    StringBuilder regex = new StringBuilder();
    for (int i = 0; i < wildcard.length(); i++) {
        char c = wildcard.charAt(i);
        if (c == '*') {
            regex.append(".*");
        } else if (c == '?') {
            regex.append(".");
        } else if ("+()^$.{}[]|\\".indexOf(c) >= 0) {
            regex.append("\\").append(c);
        } else {
            regex.append(c);
        }
    }
    return regex.toString();
}

