import org.apache.ws.security.components.crypto.X509CertificateCrypto;

import javax.crypto.SecretKey;
import javax.xml.namespace.QName;
import org.apache.wss4j.common.crypto.EncryptionProperties;

public EncryptionProperties getEncryptionProperties() throws Exception {
    // Initialize the crypto object with the keystore containing the encryption key and certificate
    Crypto crypto = CryptoFactory.getInstance("myKeystore.properties");

    // Get the encryption key
    CryptoType cryptoType = new CryptoType(CryptoType.TYPE.ALIAS);
    cryptoType.setAlias("my-encryption-key-alias");
    SecretKey encryptionKey = crypto.getSecretKey("my-keystore-password", cryptoType);

    // Get the recipient certificate
    cryptoType = new CryptoType(CryptoType.TYPE.ALIAS);
    cryptoType.setAlias("recipient-certificate-alias");
    X509Certificate certificate = (X509Certificate)crypto.getX509Certificates(cryptoType)[0];

    // Set the encryption properties
    EncryptionProperties encryptionProperties = new EncryptionProperties();
    encryptionProperties.setEncryptionSymAlgorithm("http://www.w3.org/2001/04/xmlenc#aes128-cbc");
    encryptionProperties.setEncryptionKey(encryptionKey);
    encryptionProperties.setEncryptionUser(certificate);

    return encryptionProperties;
}

