import java.io.IOException;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLEncoder;
import java.util.Base64;

public class AccessTokenGetter {

  public static String getAccessToken(String clientID, String clientSecret, String scope) throws IOException {
    String tokenEndpoint = "https://oauth.example.com/token";
    String grantType = "client_credentials";
    String authorization = Base64.getEncoder().encodeToString((clientID + ":" + clientSecret).getBytes());

    // Build request parameters
    String params = "grant_type=" + URLEncoder.encode(grantType, "UTF-8") +
                    "&scope=" + URLEncoder.encode(scope, "UTF-8");

    // Create HTTP connection and set headers
    URL url = new URL(tokenEndpoint);
    HttpURLConnection conn = (HttpURLConnection) url.openConnection();
    conn.setRequestMethod("POST");
    conn.setRequestProperty("Authorization", "Basic " + authorization);
    conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");

    // Send request and read response
    conn.setDoOutput(true);
    conn.getOutputStream().write(params.getBytes());
    BufferedReader reader = new BufferedReader(new InputStreamReader(conn.getInputStream()));
    String response = reader.readLine();

    // Extract access token from response
    String accessToken = response.split("&")[0].split("=")[1];

    return accessToken;
  }

}

